%spark

// UDF : Retourne la durée en minutes à partir d'une string au format "mim:sec"
def toMinutes(timeString: String): Integer = {
    var minutes = 0;
    if( timeString != null && !timeString.isEmpty ) {
        val time =  timeString.split( ":" )
        minutes = time(0).toInt * 60 + time(1).toInt
    }
    minutes
}
sqlContext.udf.register("toMinutes", toMinutes(_:String))


val evaRdd = sqlContext.read.format("com.databricks.spark.csv").
    option("header", "true").
    option("delimiter", ",").
    option("quote", "\"").
    option("mode", "PERMISSIVE").
    option("charset", "UTF-8").
    option("inferSchema", "true").
    load("/data/eva.csv")

evaRdd.registerTempTable("evas")
